mapper.select:
SELECT sb.BID, sb.BNAME, sb.BCODE, sb.LEGALER, sb.PHONE, sb.EMAIL, sb.FAXES, sb.ADDRESS, sb.STATUS, sb.BEGINDATE, sb.ENDDATE, sb.APPID, sb.APPSECRET, sb.LICENSEID, sb.CRTER, sb.CRTDATE, sb.OPTER, sb.OPTDATE, sb.DELETED FROM wx_dish_business sb where sb.deleted = 0
and sb.BID = #{bid}
and sb.BNAME like CONCAT('%',#{bname},'%')
and sb.BCODE like CONCAT('%',#{bcode},'%')
and sb.LEGALER like CONCAT('%',#{legaler},'%')
and sb.STATUS = #{status}
limit ${pageStart},${pageSize}
limit 0,2

SELECT sb.BID, sb.BNAME, sb.BCODE, sb.LEGALER, sb.PHONE, sb.EMAIL, sb.FAXES, sb.ADDRESS, sb.STATUS, sb.BEGINDATE, sb.ENDDATE, sb.APPID, sb.APPSECRET, sb.LICENSEID, sb.CRTER, sb.CRTDATE, sb.OPTER, sb.OPTDATE, sb.DELETED FROM wx_dish_business sb where sb.deleted = 0
and sb.BID =#{bid}
and sb.BNAME like CONCAT('%',#{bname},'%')
and sb.BCODE = #{bcode}
and sb.LEGALER like CONCAT('%',#{legaler},'%')

mapper.update:
UPDATE
    wx_dish_business
SET
    OPTER = #{opter}
    , OPTDATE = sysdate()
    
		, BNAME = #{bname}
	
	
		, BCODE = #{bcode}
	
	
		, LEGALER = #{legaler}
	
	
		, PHONE = #{phone}
	
	
		, EMAIL = #{email}
	
	
		, FAXES = #{faxes}
	
	
		, ADDRESS = #{address}
	
	
		, STATUS = #{status}
	
	
		, BEGINDATE = #{begindate}
	
	
		, ENDDATE = #{enddate}
	
	
		, APPID = #{appid}
	
	
		, APPSECRET = #{appsecret}
	
	
		, LICENSEID = #{licenseid}
	
	
		, DELETED = #{deleted}
	
WHERE
    BID = #{bid}
