mapper.update:
UPDATE
    wx_dish_commet
SET
	opter = #{opter}
	, optdate = sysdate()
	
		, status = #{status}
	
	
		, replyer = #{replyer}
	
	
		, reply = #{reply}
	
	
		, replydate = #{replydate}
	
	
		, deleted = #{deleted}
	
	
		, isread = #{isread}
	
WHERE
    rid =  #{rid}

mapper.select:
select comments.from_user, fans.nickname, fans.username, fans.headimgurl, comments.description, comments.isread, comments.crtdate from wx_dish_commet comments left join wx_dish_fans fans on fans.from_user = comments.from_user left join ( select comments.from_user, max(comments.crtdate) crtdate from wx_dish_commet comments where comments.deleted = 0 and comments.`status` != -1 and comments.storeid = #{storeid} group by comments.from_user ) lastcomment on lastcomment.from_user = comments.from_user where comments.deleted = 0 and comments.storeid = #{storeid} and comments.crtdate = lastcomment.crtdate
and UPPER(concat(fans.nickname,fans.username)) like concat('%',#{keyword},'%')
and comments.crtdate >= #{begindate}
and comments.crtdate <= #{enddate}
group by comments.from_user, fans.nickname, fans.username, fans.headimgurl, comments.description, comments.isread order by comments.isread asc,comments.crtdate desc

select count(1) from ( select 1 from wx_dish_commet comments left join wx_dish_fans fans on fans.from_user = comments.from_user left join ( select comments.from_user, max(comments.crtdate) crtdate from wx_dish_commet comments where comments.deleted = 0 and comments.`status` != -1 and comments.storeid = #{storeid} group by comments.from_user ) lastcomment on lastcomment.from_user = comments.from_user where comments.deleted = 0 and comments.storeid = #{storeid} and comments.crtdate = lastcomment.crtdate
and UPPER(concat(fans.nickname,fans.username)) like concat('%',#{keyword},'%')
and comments.crtdate >= #{begindate}
and comments.crtdate <= #{enddate}
group by comments.from_user, fans.nickname, fans.username, fans.headimgurl, comments.description, comments.isread )t
