mapper.select:
SELECT goods.id, goods.weid, goods.storeid, goods.pcate, goods.ccate, goods.status, goods.recommend, goods.displayorder, goods.title, goods.thumb, goods.unitname, goods.description, goods.taste, goods.isspecial, goods.marketprice, goods.productprice, goods.subcount, goods.cid, gc.name cname, goods.crter, goods.crtdate, goods.opter, goods.optdate, goods.deleted FROM wx_dish_goods goods left join wx_dish_goods_category gc on gc.id = goods.cid where goods.deleted = 0 and gc.deleted = 0
and goods.weid = #{weid}
and goods.id = #{id}
and goods.title = #{title}
and gc.name = #{cname}
order by gc.displayorder desc,goods.displayorder desc

SELECT
    count(1)
FROM
    wx_dish_goods goods
left join wx_dish_goods_category gc on gc.id = goods.cid
where goods.deleted = 0 
and gc.deleted = 0

	and goods.weid = #{weid}


	and goods.id = #{id}
 

	and goods.title = #{title}


	and gc.name = #{cname}

SELECT goods.id, goods.weid, goods.storeid, goods.pcate, goods.ccate, goods.status, goods.recommend, goods.displayorder, goods.title, goods.thumb, goods.unitname, goods.description, goods.taste, goods.isspecial, goods.marketprice, goods.productprice, goods.subcount, goods.cid, gc.name cname, goods.crter, goods.crtdate, goods.opter, goods.optdate, goods.deleted FROM wx_dish_goods goods left join wx_dish_goods_category gc on gc.id = goods.cid where goods.deleted = 0 and gc.deleted = 0
and goods.id = #{id}
and goods.title = #{title}
and gc.name = #{cname}

mapper.update:
UPDATE
    wx_dish_tables
SET
	opter = #{opter}
	, optdate = sysdate()
	
		, limit_price = #{limit_price}
	
	
		, title = #{title}
	
	
		, room_id = #{room_id}
	
	
		, user_count = #{user_count}
	
	
		, displayorder = #{displayorder}
	
	
		, status = 0
	
	
		, status = #{status}
	
	
		, deleted = #{deleted}
	
WHERE
    id = #{id}  
    
UPDATE
    wx_dish_goods
SET
    opter = #{opter}
	, optdate = sysdate()
   
		, pcate = #{pcate}
	
	
		, ccate = #{ccate}
	
	
		, status = #{status}
	
	
		, recommend = #{recommend}
	
	
		, displayorder = #{displayorder}
	
	
		, title = #{title}
	
	
		, thumb = #{thumb}
	
	
		, unitname = #{unitname}
	
	
		, description = #{description}
	
	
		, taste = #{taste}
	
	
		, isspecial = #{isspecial}
	
	
		, marketprice = #{marketprice}
	
	
		, productprice = #{productprice}
	
	
		, subcount = #{subcount}
	
	
		, cid = #{cid}
	
	
		, deleted = #{deleted}
	
WHERE
    id = #{id};

    
UPDATE
    wx_dish_goods_category
SET
	opter = #{opter}
	, optdate = sysdate()
	
		, name = #{name}
	
	
		, parentid = #{parentid}
	
	
		, displayorder = #{displayorder}
	
	
		, enabled = #{enabled}
	
	
		, deleted = #{deleted}
	
WHERE
    id = #{id};

mapper.select:
SELECT cate.id, cate.weid, cate.storeid, cate.name, cate.parentid, cate.displayorder, cate.enabled, cate.crter, cate.crtdate, cate.opter, cate.optdate, cate.deleted FROM wx_dish_goods_category cate where cate.deleted = 0 and cate.weid = #{weid}
and cate.name = #{name}
order BY cate.displayorder desc
