mapper.select:
SELECT ord.id, ord.weid, ord.storeid, ord.from_user, ord.totalprice, ord.ordersn, ord.status, ord.ispay, ord.paytype, ord.transid, ord.username, ord.address, ord.tel, ord.meal_time, ord.counts, ord.seat_type, ord.carports, ord.realprice, ord.dining_mode, ord.remark, ord.taste, ord.tableid, tab.title as tablecode, ord.print_sta, ord.isticket, ord.title, ord.dateline, ord.crter, ord.crtdate, ord.opter, ord.optdate, ord.deleted FROM wx_dish_order ord left join wx_dish_tables tab on (tab.id = ord.tableid and tab.deleted = 0) where ord.deleted = 0
and ord.id = #{id}
and ord.weid = #{weid}
and ord.storeid = #{storeid}
and ord.ordersn like concat('%',#{ordersn},'%')
and ord.from_user = #{from_user}
and ord.dateline >= ${begindateLine}
and ord.dateline <= ${enddateLine}
order by ord.optdate desc

SELECT count(1)
FROM
    wx_dish_order ord
where ord.deleted = 0    

	and ord.id = #{id}


	and ord.weid = #{weid}


	and ord.storeid = #{storeid}


	and ord.ordersn like concat('%',#{ordersn},'%')


	and ord.from_user = #{from_user}


	and ord.dateline >= ${begindateLine}


	and ord.dateline <= ${enddateLine}

SELECT ord.id, ord.weid, ord.storeid, ord.from_user, ord.totalprice, ord.ordersn, ord.status, ord.ispay, ord.paytype, ord.transid, ord.username, ord.address, ord.tel, ord.meal_time, ord.counts, ord.seat_type, ord.carports, ord.realprice, ord.dining_mode, ord.remark, ord.taste, ord.tableid, tab.title as tablecode, ord.print_sta, ord.isticket, ord.title, ord.dateline, ord.crter, ord.crtdate, ord.opter, ord.optdate, ord.deleted FROM wx_dish_order ord left join wx_dish_tables tab on (tab.id = ord.tableid and tab.deleted = 0) where ord.deleted = 0
and ord.id = #{id}
and ord.weid = #{weid}
and ord.storeid = #{storeid}
and ord.ordersn = #{ordersn}
and ord.from_user = #{from_user}

mapper.update:
UPDATE
    wx_dish_order
SET
	opter = #{opter}
	, optdate = sysdate()
	
		, totalprice = #{totalprice}
	
	
		, status = #{status}
	
	
		, ispay = #{ispay}
	
	
		, paytype = #{paytype}
	
	
		, transid = #{transid}
	
	
		, username = #{username}
	
	
		, address = #{address}
	
	
		, tel = #{tel}
	
	
		, meal_time = #{meal_time}
	
	
		, counts = #{counts}
	
	
		, seat_type = #{seat_type}
	
	
		, carports = #{carports}
	
	
		, realprice = #{realprice}
	
	
		, dining_mode = #{dining_mode}
	
	
		, remark = #{remark}
	
	
		, taste = #{taste}
	
	
		, tableid = #{tableid}
	
	
		, print_sta = #{print_sta}
	
	
		, isticket = #{isticket}
	
	
		, title = #{title}
	
	
		, deleted = #{deleted}
	
WHERE
    id = #{id}

   UPDATE
	    wx_dish_order_detail
	SET
		opter = #{opter}
		, optdate = sysdate()
		
			, goods_id = #{goods_id}
		
		
			, num = #{num}
		
		
			, price = #{price}
		
		
			, cost = #{cost}
		
		
			, deleted = #{deleted}
		
	WHERE
	    did = #{did}
