mapper.select:
SELECT org.OID, org.ONAME, org.LEVEL, org.PARENTID, org.LEVELPATH, org.SEQ, org.BID, org.CRTER, org.CRTDATE, org.OPTER, org.OPTDATE, org.DELETED, corg.child_oid FROM self_organization org left join ( select so.PARENTID, GROUP_CONCAT(so.OID) as child_oid from self_organization so where so.DELETED = 0 group by so.PARENTID )corg on corg.PARENTID = org.OID where org.DELETED = 0 and org.BID = #{bid}
and org.PARENTID = #{parentid}
and (org.PARENTID is null or org.PARENTID = '')
order by org.LEVEL,org.SEQ desc

mapper.update:
UPDATE
    self_organization
SET
	OPTER = #{opter}
    , OPTDATE = sysdate()
    
		, ONAME = #{oname}
	
	
		, LEVEL = #{level}
	
	
		, PARENTID = #{parentid}
	
	
		, LEVELPATH = #{levelpath}
	
	
		, SEQ = #{seq}
	
	
		, DELETED = #{deleted}
	
WHERE
    OID = #{oid}

UPDATE
    self_org_emp_ref
SET
	OPTDATE = sysdate()
	, OPTER = #{opter}
	
		, EMPID = #{empid}
	
	
		, OID = #{oid}
	
	
		, deleted = #{deleted}
	
WHERE
    OID = #{oid}
    and EMPID = #{empid}

mapper.select:
select emp.EMPID, emp.EMPNAME, emp.EMPCODE, emp.STATUS from self_employee emp where emp.DELETED = 0 and emp.EMPID not in ( select oe.EMPID from self_org_emp_ref oe where oe.DELETED = 0
and oe.OID = #{oid}
) and emp.BID = #{bid}
and emp.EMPNAME like CONCAT('%',#{empname},'%')
and emp.EMPCODE = #{empcode}
