mapper.select:
SELECT store.id, store.weid, store.title, store.logo, store.info, store.announce, store.tel, store.location_p, store.location_c, store.location_a, store.address, store.lat, store.lng, store.password, store.hours, store.recharging_password, store.thumb_url, store.enable_wifi, store.enable_card, store.enable_room, store.enable_park, store.is_rest, store.is_show, store.is_meal, store.is_delivery, store.is_reservation, store.is_queue, store.delivery_within_days, store.delivery_radius, store.not_in_delivery_radius, store.sendingprice, store.consume, store.level, store.updatetime, store.dateline, store.content, store.begintime, store.endtime FROM wx_dish_stores store where 1 = 1
and store.id = #{id}
and store.weid = #{weid}

mapper.update:
UPDATE
    wx_dish_stores
SET
    id = #{id}
    
		, weid = #{weid}
	
	
		, title = #{title}
	
	
		, logo = #{logo}
	
	
		, info = #{info}
	
	
		, announce = #{announce}
	
	
		, tel = #{tel}
	
	
		, location_p = #{location_p}
	
	
		, location_c = #{location_c}
	
	
		, location_a = #{location_a}
	
	
		, address = #{address}
	
	
		, lat = #{lat}
	
	
		, lng = #{lng}
	
	
		, password = #{password}
	
	
		, hours = #{hours}
	
	
		, recharging_password = #{recharging_password}
	
	
		, thumb_url = #{thumb_url}
	
	
		, enable_wifi = #{enable_wifi}
	
	
		, enable_card = #{enable_card}
	
	
		, enable_room = #{enable_room}
	
	
		, enable_park = #{enable_park}
	
	
		, is_rest = #{is_rest}
	
	
		, is_show = #{is_show}
	
	
		, is_meal = #{is_meal}
	
	
		, is_delivery = #{is_delivery}
	
	
		, is_reservation = #{is_reservation}
	
	
		, is_queue = #{is_queue}
	
	
		, delivery_within_days = #{delivery_within_days}
	
	
		, delivery_radius = #{delivery_radius}
	
	
		, not_in_delivery_radius = #{not_in_delivery_radius}
	
	
		, sendingprice = #{sendingprice}
	
	
		, consume = #{consume}
	
	
		, level = #{level}
	
	
		, bid = #{bid}
	
	
		, updatetime = #{updatetime}
	
	
		, dateline = #{dateline}
	
	
		, content = #{content}
	
	
		, begintime = #{begintime}
	
	
		, endtime = #{endtime}
	
WHERE
    id = #{id}
