mapper.select:
SELECT WID, WCODE, WVALUE, WTEXT, PARENTID, SEQ, CONTENT, CRTDATE, CRTER, DELETED FROM auto_work_dict where deleted = 0
and WID = #{wid}
and WCODE = #{wcode}
and PARENTID = #{parentid}
and WVALUE = #{wvalue}
and WTEXT = #{wtext}
and PARENTID = (select wid from auto_work_dict where deleted = 0 and wcode = #{parentcode} and wvalue = 'root')
order by SEQ desc

mapper.update:
UPDATE auto_work_dict SET 
	WID = #{wid}
	
		, WCODE = #{wcode}
	
	
		, WVALUE = #{wvalue}
	 
	
		, WTEXT = #{wtext}
	 
	
		, SEQ = #{seq}
	 
	
		, CONTENT = #{content}
	
	
		, DELETED = #{deleted}
	
	
		, PARENTID = #{parentid}
		
WHERE WID = #{wid}

mapper.select:
 SELECT items.PID,
       	(select wtext from auto_work_dict where wvalue = items.TYPE and deleted = 0) type,
       	(select wtext from auto_work_dict where wvalue = items.BRAND and deleted = 0) brand,
       items.PCODE,
       items.PNAME,
       items.PRICE,
       items.COMPRICE,
       items.COSTPRICE,
       items.UNIT,
       items.REMARK,
       items.CONTENT,
       items.IMGID,
       items.CRTDATE,
       items.CRTER,
       items.DELETED
  FROM auto_parts_items items
  where 1 = 1 
  
	  and items.PID = #{pid}
  
  
	  and items.TYPE = #{type}
  
  
	  and items.BRAND = #{brand}
  
  
	  and items.PCODE = #{pcode}
  
  
	  and items.PNAME = #{pname}
  
  
	  and items.PRICE = #{price}
  
  
	  and items.COMPRICE = #{comprice}
  
  
	  and items.COSTPRICE = #{costprice}
  
  
	  and items.UNIT = #{unit}
  
  
	  and items.REMARK = #{remark}
  
  
	  and items.CONTENT = #{content}
  
  
	  and items.IMGID = #{imgid}

mapper.insert:
INSERT INTO auto_parts_items (
	PID
	,TYPE
	,BRAND
	,PCODE
	,PNAME
	,PRICE
	,COMPRICE
	,COSTPRICE
	,UNIT
	,REMARK
	,CONTENT
	,IMGID
	,CRTDATE
	,CRTER
	,DELETED
	)
VALUES (
	replace(uuid(),'-','')
	,#{type}
	,#{brand}
	,#{pcode}
	,#{pname}
	,#{price}
	,#{comprice}
	,#{costprice}
	,#{unit}
	,#{remark}
	,#{content}
	,#{imgid}
	,sysdate()
	,'admin'
	,0
	)

mapper.update:
UPDATE auto_parts_items
   SET PID       =  #{pid}
   	  
		  , TYPE = #{type}
	  
	  
		  , BRAND = #{brand}
	  
	  
		  , PCODE = #{pcode}
	  
	  
		  , PNAME = #{pname}
	  
	  
		  , PRICE = #{price}
	  
	  
		  , COMPRICE = #{comprice}
	  
	  
		  , COSTPRICE = #{costprice}
	  
	  
		  , UNIT = #{unit}
	  
	  
		  , REMARK = #{unit}
	  
	  
		  , CONTENT = #{content}
	  
	  
		  , IMGID = #{imgid}
	  
	  
		  , DELETED = #{deleted}
	  
 WHERE PID = #{pid}
