mapper.select:
SELECT tab.id, tab.weid, tab.storeid, tab.limit_price, tab.title, tab.user_count, tab.qrcodeid, room.rname room_name, tab.displayorder, tab.dateline, tab.status, tab.crter, tab.crtdate, tab.opter, tab.optdate, tab.deleted FROM wx_dish_tables tab left join wx_dish_room room on (room.id = tab.room_id and room.deleted = 0) where tab.DELETED = 0
and tab.weid = #{weid}
and tab.storeid = #{storeid}
and UPPER(tab.title) = UPPER(#{title})
and room.id = #{room_id}
and tab.status = #{status}
and tab.user_count >= #{user_count}
order by tab.displayorder desc

SELECT
        count(1)
FROM
    wx_dish_tables tab
left join wx_dish_room room on (room.id = tab.room_id and room.deleted = 0)
where tab.DELETED = 0


	and tab.weid = #{weid}
 

	and tab.storeid = #{storeid}
 

	and UPPER(tab.title) = UPPER(#{title})
 

	and tab.user_count >= #{user_count}

SELECT tab.id, tab.weid, tab.storeid, tab.limit_price, tab.title, tab.user_count, tab.qrcodeid, room.id room_id, room.rname room_name, tab.displayorder, tab.dateline, tab.status, tab.crter, tab.crtdate, tab.opter, tab.optdate, tab.deleted FROM wx_dish_tables tab left join wx_dish_room room on (room.id = tab.room_id and room.deleted = 0) where tab.DELETED = 0
and tab.id = #{id}
and tab.weid = #{weid}
and tab.storeid = #{storeid}
and UPPER(tab.title) = UPPER(#{title})
and tab.user_count >= #{user_count}

mapper.update:
UPDATE
    wx_dish_tables
SET
	opter = #{opter}
	, optdate = sysdate()
	
		, limit_price = #{limit_price}
	
	
		, title = #{title}
	
	
		, room_id = #{room_id}
	
	
		, user_count = #{user_count}
	
	
		, displayorder = #{displayorder}
	
	
		, status = 0
	
	
		, status = #{status}
	
	
		, deleted = #{deleted}
	
WHERE
    id = #{id}

UPDATE
    wx_dish_room
SET
    opter = #{opter}
	, optdate = sysdate()
	
		, rname = #{room_name}
	
	
		, deleted = #{deleted}
	
WHERE
    id = #{room_id}

mapper.select:
SELECT tab.id, tab.weid, tab.storeid, tab.limit_price, tab.title, tab.user_count, tab.qrcodeid, room.rname room_name, tab.displayorder, tab.dateline, tab.status, tab.crter, tab.crtdate, tab.opter, tab.optdate, tab.deleted FROM wx_dish_tables tab left join wx_dish_room room on (room.id = tab.room_id and room.deleted = 0) where tab.DELETED = 0
and tab.weid = #{weid}
and tab.storeid = #{storeid}
and UPPER(tab.title) like CONCAT('%',UPPER(#{title}),'%')
and room.id = #{room_id}
and tab.status = #{status}
and tab.user_count >= #{user_count}
order by tab.displayorder desc
